import React, { useEffect } from 'react';

function GetQuote() {
  // Initialize Google Maps Places API
  useEffect(() => {
    const loadGoogleMaps = () => {
      const autocompleteInput = document.getElementById('location');
      const autocomplete = new window.google.maps.places.Autocomplete(autocompleteInput, {
        types: ['address'], // Restrict to addresses
        componentRestrictions: { country: 'us' }, // Restrict to the US (adjust as needed)
      });

      autocomplete.addListener('place_changed', () => {
        const place = autocomplete.getPlace();
        console.log('Selected place:', place);
      });
    };

    if (window.google) {
      loadGoogleMaps();
    } else {
      const script = document.createElement('script');
      script.src = `https://maps.googleapis.com/maps/api/js?key=YOUR_GOOGLE_MAPS_API_KEY&libraries=places`;
      script.async = true;
      script.onload = loadGoogleMaps;
      document.head.appendChild(script);
    }
  }, []);

  return (
    <div className="bg-[#F4F1DE] text-center py-10">
      <h2 className="text-2xl font-bold text-[#4A7A63]">Get a Quote</h2>
      <p className="text-lg text-[#4A7A63] mt-4">
        Fill out the form below, and weâ€™ll provide an estimate for your project.
      </p>
      <form className="mt-6 space-y-4 max-w-md mx-auto">
        {/* Name */}
        <input
          type="text"
          placeholder="Your Name"
          className="w-full px-4 py-2 border border-[#4A7A63] rounded"
        />

        {/* Email */}
        <input
          type="email"
          placeholder="Your Email"
          className="w-full px-4 py-2 border border-[#4A7A63] rounded"
        />

        {/* Phone Number */}
        <input
          type="tel"
          placeholder="Your Phone Number"
          className="w-full px-4 py-2 border border-[#4A7A63] rounded"
        />

        {/* Project Type */}
        <select
          className="w-full px-4 py-2 border border-[#4A7A63] rounded"
          defaultValue=""
        >
          <option value="" disabled>
            Select Project Type
          </option>
          <option value="landscaping">Landscaping</option>
          <option value="irrigation">Irrigation</option>
          <option value="design">Design</option>
          <option value="maintenance">Maintenance</option>
          <option value="other">Other</option>
        </select>

        {/* Project Description */}
        <textarea
          placeholder="Describe your project"
          className="w-full px-4 py-2 border border-[#4A7A63] rounded"
          rows="4"
        ></textarea>

        {/* Square Footage */}
        <input
          type="number"
          placeholder="Square Footage (e.g., 500)"
          className="w-full px-4 py-2 border border-[#4A7A63] rounded"
        />

        {/* Location (Google Maps API Auto-complete) */}
        <input
          type="text"
          id="location"
          placeholder="Your Address"
          className="w-full px-4 py-2 border border-[#4A7A63] rounded"
        />

        {/* Date/Time Picker */}
        <input
          type="datetime-local"
          className="w-full px-4 py-2 border border-[#4A7A63] rounded"
        />

        {/* Submit Button */}
        <button
          type="submit"
          className="bg-[#E07A5F] text-[#F4F1DE] px-6 py-2 rounded hover:bg-[#d46653]"
        >
          Submit
        </button>
      </form>
    </div>
  );
}

export default GetQuote;
